import React, {useState, useEffect} from 'react';
import {
    withGoogleMap,
    withScriptjs,
    GoogleMap,
    Marker,
    InfoWindow,
    Polyline
  } from "react-google-maps";
import { compose, withProps } from "recompose";



function ClinicMap(props) {
  const [showInfo , setShowInfo] = useState(null);
  console.log(showInfo)
  const MyMapComponent = compose(
    withProps({
      googleMapURL: "https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyB3P3RbY7UksatT5DcJgFvYlciUsUtM1zw",
      loadingElement: <div style={{ height: `100%` }} />,
      containerElement: <div style={{ height: `100%` }} />,
      mapElement: <div style={{ height: `100%` }} />,
    }),
    withScriptjs,
    withGoogleMap
  )((props) =>
    <GoogleMap
      defaultZoom={15}
      center={{lat: props.marker.lat, lng: props.marker.lng}}
      // defaultCenter={{ lat: 35.689487, lng: 139.691711 }}

    >
           {props.marker && <Marker 
          position={{lat: props.marker.lat, lng: props.marker.lng}}
          onClick={()=>{ setShowInfo(props.marker);}

          }
          icon={{
            url: `/health-clinic.png`,
            scaledSize: new window.google.maps.Size(50, 50)
          }}
          />}

     {showInfo && (
     <InfoWindow
        onCloseClick={() => {
          setShowInfo(false);
        }}
        position={{lat: props.marker.lat, lng: props.marker.lng}}
      >
        <div>
          <h2>hhhh</h2>
          <p>density :</p>
          <p>popularity :</p>
          <p>demographic :</p>
        </div>
      </InfoWindow>
   )} 
    </GoogleMap>
  )

  return(
  <>






<div style={{ width: "100vw", height: "100vh" }}>
<MyMapComponent marker={props.marker}

      />

</div>

    {/* <div style={{ width: "100vw", height: "100vh" }}>
      <MapWrapped
        googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=
        AIzaSyB3P3RbY7UksatT5DcJgFvYlciUsUtM1zw`}
        loadingElement={<div style={{ height: `100%` }} />}
        containerElement={<div style={{ height: `100%` }} />}
        mapElement={<div style={{ height: `100%` }} />}
      />
    </div> */}
    </>
  );
}
export default ClinicMap
