import React from 'react';
import './addressarea.css';
import * as clinicData from "../../data/clinic-location.json";
import $ from 'jquery'; 


export default class AddressArea extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
          postcode: '',
          prefecture: '',
          city: '',
          area: '',
          street: '',
          lineTwo: '',
          latitude: '',
          longtitude: '',
          setMarker: props.setMarker,
          setMarkerInfo: props.setMarkerInfo
        }
        this.setPostcode = this.setPostcode.bind(this);
        this.searchByPostcode = this.searchByPostcode.bind(this);
      }
      componentDidMount() {

      }
      setPostcode(event){
          this.setState({postcode: event.target.value})
      }
      searchByPostcode(postcode){
        //clear the previous value
        let a= '';
        let b='';
        let c='';
        this.setState({
            prefecture:a,
            city: b,
            area: c,
            street: '',
            lineTwo: ''
      })
